import React, { Component } from "react";
import classnames from "classnames";

class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      address: "",
      email: "",
      // password: "",
      // passwordConfir: "",
      errors: {},
      // isloading: false,
      // isvalid:false,
    };
  }

  state = {};

  render() {
    const { errors } = this.state

    return (
      <div className="col-md-12">
        <h2 align="center">Request pickup form</h2>
        <form>
          <div className="form-group">
            <label className="control-label">Name </label>
            <input
              className={classnames("form-control", {
                "is-invalid": errors.name,
              })}
              type="text"
              name="name"
              value={this.state.name}
              onChange={this.onChange}
            />
            {errors.name && (
              <span className="from-text text-muted">{errors.name}</span>
            )}
          </div>

          {/* <h3>Basic information</h3>
          <div className="row">
            <label>Name</label>
            <input type="text"></input>
            <label>Address</label>
            <input type="text"></input>
            <label>Email address</label>
            <input type="text"></input>
            <label>Phone number</label>
            <input type="text"></input>
          </div> */}

          <h3>Delivery information</h3>
          <label>Choose returning company</label>
          <select>
            <option>Amazon</option>
            <option>eBay</option>
            <option>Walmart</option>
            <option>Best Buy</option>
            <option>Costco</option>
            <option>Newegg</option>
            <option>Other</option>
          </select>
          {/* Insert a text input box when use picked other */}

          {/* Insert more times */}
          <label>Choose pickup time</label>
          <select>
            <option>9:00am - 10:00am</option>
            <option>10:00am - 11:00am</option>
            <option>11:00am - 12:00pm</option>
            <option>12:00pm - 1:00pm</option>
            <option>1:00pm - 2:00pm</option>
            <option>2:00pm - 3:00pm</option>
            <option>3:00pm - 4:00pm</option>
            <option>4:00pm - 5:00pm</option>
          </select>
          {/* <label>Regular or express delivery</label>
          <select>
            <option>Regular</option>
            <option>Express</option>
          </select> */}

          <h3>Package information</h3>
          <label>Type of returning product</label>
          <input type="text"></input>
          {/* Requires to disable user input, e.g. -1 */}
          <label>Length</label>
          <input type="number" min="0"></input>
          <label>Width</label>
          <input type="number" min="0"></input>
          <label>Height</label>
          <input type="number" min="0"></input>
          <label>Weight</label>
          <input type="number" min="0"></input>
          <input
            type="checkbox"
            id="saveInformation"
            name="saveInformation"
            value="saveInformationTrue"
          />
          <label for="saveInformation">
            Would you like to save your information?
          </label>
          <button>Submit</button>
        </form>
      </div>
    );
  }
}

export default Form;
