import React, { Component } from "react";
import classnames from "classnames";

class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      address: "",
      email: "",
      phoneNumber: "",
      returnCompany: "",
      pickUpTime: "",
      returnProductType: "",
      // password: "",
      // passwordConfir: "",
      errors: {},
      // isloading: false,
      isvalid: false,
    };
  }

  state = {};

  render() {
    const { errors } = this.state;

    return (
      <div className="col-md-12">
        <h2 align="center">Request pickup form</h2>
        <form>
          <h3>Basic information</h3>
          <div className="form-group">
            <label className="control-label">Name </label>
            <input
              className={classnames("form-control", {
                "is-invalid": errors.name,
              })}
              type="text"
              name="name"
              value={this.state.name}
              onChange={this.onChange}
            />
            {errors.name && (
              <span className="from-text text-muted">{errors.name}</span>
            )}
          </div>

          <div className="form-group">
            <label className="control-label">Email </label>
            <input
              className={classnames("form-control", {
                "is-invalid": errors.email,
              })}
              type="text"
              name="email"
              value={this.state.email}
              onChange={this.onChange}
            />
            {errors.email && (
              <span className="from-text text-muted">{errors.email}</span>
            )}
          </div>

          <div className="form-group">
            <label className="control-label">Address </label>
            <input
              className={classnames("form-control", {
                "is-invalid": errors.address,
              })}
              type="text"
              name="address"
              value={this.state.address}
              onChange={this.onChange}
            />
            {errors.address && (
              <span className="from-text text-muted">{errors.address}</span>
            )}
          </div>

          <div className="form-group">
            <label className="control-label">Phone number </label>
            <input
              className={classnames("form-control", {
                "is-invalid": errors.phoneNumber,
              })}
              type="text"
              name="phoneNumber"
              value={this.state.phoneNumber}
              onChange={this.onChange}
            />
            {errors.phoneNumber && (
              <span className="from-text text-muted">{errors.phoneNumber}</span>
            )}
          </div>

          <h3>Delivery information</h3>
          <div className="form-group">
            <label className="control-label">Choose returning company </label>
            <select
              className={classnames("form-control", {
                "is-invalid": errors.returnCompany,
              })}
              name="returnCompany"
              onChange={this.onChange}
            >
              <option value="amazon">Amazon</option>
              <option value="ebay">eBay</option>
              <option value="walmart">Walmart</option>
              <option value="bestbuy">Best Buy</option>
              <option value="costco">Costco</option>
              <option value="newegg">Newegg</option>
              <option value="other">Other</option>
            </select>
            {errors.returnCompany && (
              <span className="from-text text-muted">
                {errors.returnCompany}
              </span>
            )}
          </div>

          {/* Insert a text input box when use picked other */}

          <div className="form-group">
            <label className="control-label">Choose pickup time </label>
            <select
              className={classnames("form-control", {
                "is-invalid": errors.pickUpTime,
              })}
              name="pickUpTime"
              onChange={this.onChange}
            >
              <option value="9am-10am">9:00am - 10:00am</option>
              <option value="10am-11am">10:00am - 11:00am</option>
              <option value="11am-12pm">11:00am - 12:00pm</option>
              <option value="12pm-1pm">12:00pm - 1:00pm</option>
              <option value="1pm-2pm">1:00pm - 2:00pm</option>
              <option value="2pm-3pm">2:00pm - 3:00pm</option>
              <option value="3pm-4pm">3:00pm - 4:00pm</option>
              <option value="4pm-5pm">4:00pm - 5:00pm</option>
            </select>
            {errors.pickUpTime && (
              <span className="from-text text-muted">{errors.pickUpTime}</span>
            )}
          </div>

          <h3>Package information</h3>
          <div className="form-group">
            <label className="control-label">Type of returning product </label>
            <input
              className={classnames("form-control", {
                "is-invalid": errors.returnProductType,
              })}
              type="text"
              name="name"
              value={this.state.returnProductType}
              onChange={this.onChange}
            />
            {errors.returnProductType && (
              <span className="from-text text-muted">{errors.returnProductType}</span>
            )}
          </div>

          <div className="form-group">
            <label className="control-label">Length </label>
            <input
              className={classnames("form-control", {
                "is-invalid": errors.packageLength,
              })}
              type="number"
              name="packageLength"
              value={this.state.packageLength}
              onChange={this.onChange}
            />
            {errors.packageLength && (
              <span className="from-text text-muted">{errors.packageLength}</span>
            )}
          </div>

          <h3>Package information</h3>
          <label>Type of returning product</label>
          <input type="text"></input>
          {/* Requires to disable user input, e.g. -1 */}
          <label>Length</label>
          <input type="number" min="0"></input>
          <label>Width</label>
          <input type="number" min="0"></input>
          <label>Height</label>
          <input type="number" min="0"></input>
          <label>Weight</label>
          <input type="number" min="0"></input>
          <input
            type="checkbox"
            id="saveInformation"
            name="saveInformation"
            value="saveInformationTrue"
          />
          <label for="saveInformation">
            Would you like to save your information?
          </label>
          <button>Submit</button>
        </form>
      </div>
    );
  }
}

export default Form;
